//email validation

const validation = (userData, errors, setErrors) => {
  if(!userData.email){
    setErrors({...errors, email: "Completa este campo"})
  } else if (userData.email.length > 35){
    setErrors({...errors, email: "No puede superar los 35 caracteres"})
  } else if(!/\S+@\S+\.\S+/.test(userData.email)){
    setErrors({...errors, email: "Email inválido"})
  } else{
    setErrors({...errors, email: ""})
  }
  
  //password validation

  if(!userData.password){
    setErrors({...errors, password: "Completa este campo"})
  } else if(!/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&])[A-Za-z\d$@$!%*?&]{6,10}$/.test(userData.password))
  {
   setErrors({...errors, password: "Debe tener entre 6 y 10 caracteres"})
  } else{
    setErrors({...errors, password: ""})
  }
}


export default validation;
